import './App.css';
import DragDropFiles  from "./DragDropFiles"

const uploaded = true;
const pdf = "http://localhost:3000/Résumé.pdf";
const App = () => {

  const download = (url)=>
    {
      const filename = url.split("/").pop();
      const aTag = document.createElement("a");
      aTag.href = url;
      aTag.setAttribute("download",filename);
      document.body.appendChild(aTag);
      aTag.click();
      aTag.remove();
  };

  if(uploaded){
    return (
    <div className="final">
      <h1>Here is your consise summary of the meeting in pdf format.</h1>
      <button onClick={()=>{download(pdf);}}>Download</button>
    </div>
    )

  }
  else {
    return (
    <div className="container">
      <DragDropFiles />
    </div>
  )
}
};
export default App;
