import { useRef, useState } from "react";

const DragDropFiles = () => {
    const [files, setFiles] = useState(null);
    const [upload, setupload] = useState(null);
    const inputRef = useRef();
    const pdf = "http://localhost:3000/Résumé.pdf";
    const handleDragOver = (event) =>{
        event.preventDefault();
    };
    const handleDrop = (event) => {
        event.preventDefault();
        setFiles(event.dataTransfer.files)
    };
    const handleClick = (event) =>{
        event.preventDefault();
        setupload(event.dataTransfer.upload);
        };
    

    const Download = (url)=>
        {
          const filename = url.split("/").pop();
          const aTag = document.createElement("a");
          aTag.href = url;
          aTag.setAttribute("download",filename);
          document.body.appendChild(aTag);
          aTag.click();
          aTag.remove();
      };
        

    if (files && !upload) return (
        <div className="uploads">
            <ul>
                {Array.from(files).map((file,idx) => <li key={idx}>{file.name}</li>)}
            </ul>
            <div className="actions">
                <button onClick={() => setFiles(null)}>Cancel</button>
                <button onClick={handleClick }>Upload</button>
            </div>
        </div>
    )
    else if (files && upload) return (
        <div classname = "text">
        <h1>Here is your consise summary of the meeting in pdf format.</h1>
        <div className='butttons'>
        <button onClick={()=>{Download(pdf);}}>Download</button>
        </div>
        </div>
        )

    return (
        <>
        {!files &&(
            <div 
                className="dropzone"
                onDragOver={handleDragOver}
                onDrop={handleDrop}
            >
                <h1> Drag and Drop Files to Upload</h1>
                <h1>OR</h1>
                <input 
                    type="file"
                    onChange = {(event) => setFiles(event.target.files)}
                    hidden
                    ref={inputRef}
                />
            
                <button onClick={() => inputRef.current.click()}>Select Files</button>
            </div>
          )}
        </>
    );
};

export default DragDropFiles;
