import { useRef, useState} from "react";
import './App.css'
import Result from "./processing";
const DragDropFiles = () => {
    const [files, setFiles] = useState(null);
    const [upload, setupload] = useState(null);
    const inputRef = useRef();
    const handleDragOver = (event) =>{
        event.preventDefault();
    };
    const handleDrop = (event) => {
        event.preventDefault();
        setFiles(event.dataTransfer.files[0])
    };
    const handleClick = async(event) =>{
        <div className="waiting">
            <h1>Here is your consise summary of the meeting in pdf format.</h1>
    </div>
        event.preventDefault();
        const formData = new FormData();
        formData.append("file",files);
        try {
        const endpoint = "http://localhost:8000/upload/"
        const response = await fetch(endpoint,{
            method: "POST",
            body: formData
        });
        if (response.ok){setupload(files); console.log("file uploaded");}
        else {console.log("file not uploaded");}
        }
        catch(error){
        console.log(error);
        }
        };
    
    

    if (files && !upload) return (<>{!files && (
                    <div className="processing">
                    <h1>Please waitt while we summarize your text</h1>
                    {handleClick}
                    </div>
                    ) }
                    </>)
    else if (files && upload) return (
        <div>{Result() }</div>
        
        )

    return (
        <>
        {!files &&(
            


            <div 
                className="dropzone"
                onDragOver={handleDragOver}
                onDrop={handleDrop}
            >
                <h1> Drag and Drop Files to Upload</h1>
                <h1>OR</h1>
                <input 
                    type="file"
                    onChange = {(event) => setFiles(event.target.files[0])}
                    hidden
                    ref={inputRef}
                />
            
                <button onClick={() => inputRef.current.click()}>Select Files</button>
            </div>
          )}
        </>
    );
};

export default DragDropFiles;
